import React from 'react';

class BeerTypes extends React.Component {
    render(){
        return(
            <div className="margin-top">

            {!this.props.beerTypes || this.props.beerTypes.loadingBeerTypes ? 
                (<img alt="loading" src="data:image/gif;base64," />) : 
                (<select class="form-control ml-4" id="sel1" onChange={this.props.handleBeerTypeChange}>
                    <option disabled selected value> -- Wybierz typ piwa -- </option>
                    {this.props.beerTypes.map((beerType, index) => 
                        <option key={beerType.id} value={beerType.id}>{beerType.name}</option>
                    )};
                    <option value="inny"> -- Inny -- </option>
                </select>)}
                
            </div>
        );
    }
}

export default BeerTypes;

