import React from 'react';

class Breweries extends React.Component {
    render(){
        return(
            <div className="margin-top">

            {!this.props.breweries || this.props.breweries.loadingBreweries ? 
                (<img alt="loading" src="data:image/gif;base64," />) : 
                (<select class="form-control ml-4" id="sel1" onChange={this.props.handleBreweryChange}>
                    <option disabled selected value> -- Wybierz browar -- </option>
                    {this.props.breweries.map((brewery, index) => 
                        <option key={brewery.id} value={brewery.id}>{brewery.name}</option>
                    )};
                    <option value="inny"> -- Inny -- </option>
                </select>)}
                
            </div>
        );
    }
}

export default Breweries;

